import React from 'react'
const Course=({ courses }) => {
  //console.log(courses)
  return (
    <>
     <h1>{courses.name}</h1>
     {courses.parts
        .map(x => <p key={x.id}>{x.name +" "+ x.exercises} </p>)
        .reduce((item,index) =>{
          item[index[0]]=[]
          return item
        },{})
        }
    {/* <Total parts={courses.parts}/> */}
   </>
  )
}

 const Total = ({ parts })=>{
  //  console.log(parts);
   return(
     <>
    <p>Total of <b>{parts.reduce((sum, part)=> sum + part.exercises, 0)}</b> exercises</p> 
     </>
   )

}


const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    }, 
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]


  return <Course courses={courses} />
}
export default App
