const Courses = ({ courses }) => {
 // console.log(courses)
  return (
    <>

     {courses.map(course => <Course course={course} />)}


    {/* <Total parts={courses.parts}/> */}
   </>
  )
}
const Course = ({course})=>{
  console.log(course)
return(
  <>
  {course.name}
    {/* {course.map(x => <p key={x.id}>{x.name +" "+ x.exercises} </p>)} */}
     {/* {course.map(c => <h1 key={c.id}>{c.name}</h1> )} */} */}
  </>
)
}
 const Total = ({ parts })=>{
  //  console.log(parts);
   return(
     <>
    <p>Total of <b>{parts.reduce((sum, part)=> sum + part.exercises, 0)}</b> exercises</p> 
     </>
   )

}


const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    }, 
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]


  return <Courses courses={courses} />

}
export default App
